    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        CruiseForm cruise=new CruiseForm();
        cruise.setID(jTextField1.getText());
        cruise.setName(jTextField2.getText());
        cruise.setDate(jTextField3.getText());
        cruise.setrDate(jTextField4.getText());
        cruise.setDPort(jTextField5.getText());
        cruise.setEPort(jTextField6.getText());
        cruise.setCost(jTextField7.getText());
        AdelaideDB ad=new AdelaideDB();
        ad.insertCruise(cruise);
        JOptionPane.showMessageDialog(jTextField2, "Data Added Successfully!!!");
        
    }//GEN-LAST:event_jButton1ActionPerformed

