    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       AdelaideDB ad=new AdelaideDB();
       CruiseForm cf=new CruiseForm ();
       cf.setID(jTextField1.getText());
       ad.displayCruise(cf);
       jTextField2.setText(cf.getName());
       jTextField3.setText(cf.getDate());
       jTextField4.setText(cf.getrDate());
       jTextField5.setText(cf.getDPort());
       jTextField6.setText(cf.getDPort());
       jTextField7.setText(cf.getCost());
    }//GEN-LAST:event_jButton3ActionPerformed

