    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        PassengerForm passenger=new PassengerForm();
        passenger.setCID(jTextField1.getText());
        passenger.setID(jTextField8.getText());
        passenger.setName(jTextField9.getText());
        passenger.setAdd(jTextField10.getText());
        passenger.setNation(jTextField11.getText());
        passenger.setDOB(jTextField12.getText());
        passenger.setSailed(jTextField13.getText());
        
        AdelaideDB ad=new AdelaideDB();
        ad.insertPassenger(passenger);
        JOptionPane.showMessageDialog(jTextField8, "Data Added Successfully!!!");
        
         
    }//GEN-LAST:event_jButton2ActionPerformed

