    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        AdelaideDB ad=new AdelaideDB();
       PassengerForm pf=new PassengerForm ();
       pf.setID(jTextField8.getText());
       ad.displayPassenger(pf);
       jTextField9.setText(pf.getName());
       jTextField10.setText(pf.getAdd());
       jTextField11.setText(pf.getNation());
       jTextField12.setText(pf.getDOB());
       jTextField13.setText(pf.getSailed());
    }//GEN-LAST:event_jButton3ActionPerformed

