    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        PortForm port=new PortForm();
        port.setCID(jTextField6.getText());
        port.setName(jTextField1.getText());
        port.setCountry(jTextField2.getText());
        port.setPop(jTextField3.getText());
        port.setPReq(jTextField4.getText());
        port.setDFee(jTextField5.getText());
        
        AdelaideDB ad=new AdelaideDB();
        ad.insertPort(port);
        JOptionPane.showMessageDialog(jTextField1, "Data Added Successfully!!!");
        
    }//GEN-LAST:event_jButton2ActionPerformed

