    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       AdelaideDB ad=new AdelaideDB();
       PortForm pf=new PortForm ();
       pf.setName(jTextField1.getText());
       ad.displayPort(pf);
       jTextField2.setText(pf.getCountry());
       jTextField3.setText(pf.getPop());
       jTextField4.setText(pf.getPReq());
       jTextField5.setText(pf.getDFee());
      
    }//GEN-LAST:event_jButton1ActionPerformed

