    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        SailorForm sailor=new SailorForm();
        sailor.setCID(jTextField7.getText());
        sailor.setID(jTextField1.getText());
        sailor.setName(jTextField2.getText());
        sailor.setDOB(jTextField3.getText());
        sailor.setNation(jTextField4.getText());
        sailor.setSupervisor(jTextField5.getText());
        sailor.setSuperName(jTextField6.getText());
        AdelaideDB ad=new AdelaideDB();
        ad.insertSailor(sailor);
        JOptionPane.showMessageDialog(jTextField2, "Data Added Successfully!!!");
    }//GEN-LAST:event_jButton1ActionPerformed

