    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       AdelaideDB ad=new AdelaideDB();
       SailorForm sf=new SailorForm ();
       sf.setID(jTextField1.getText());
       ad.displaySailor(sf);
       jTextField2.setText(sf.getName());
       jTextField3.setText(sf.getDOB());
       jTextField4.setText(sf.getNation());
       jTextField5.setText(sf.getSupervisor());
       jTextField6.setText(sf.getSuperName());
    }//GEN-LAST:event_jButton3ActionPerformed

